package com.bazl.alims.utils;

import org.apache.commons.lang3.StringUtils;

import javax.imageio.ImageIO;
import javax.xml.bind.DatatypeConverter;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.IOException;

/**
 * Created by Administrator on 2017/1/2.
 */
public class ImgUtils {

    public static String generateImage(String imgStr, String path) {
        BufferedImage image = null;
        byte[] imageByte = null;
        String imgType =  "";
        if(StringUtils.isNotBlank(imgStr)){
            String[] img = imgStr.split(",");
            imgStr = img[1];
            imgType = img[0].substring(img[0].indexOf("/")+1, img[0].indexOf(";"));
        }else{
            return null;
        }
        path = path+System.currentTimeMillis()+"."+imgType;

        try {
            imageByte = DatatypeConverter.parseBase64Binary(imgStr);
            ByteArrayInputStream bis = new ByteArrayInputStream(imageByte);
            image = ImageIO.read(new ByteArrayInputStream(imageByte));
            bis.close();

            File outputfile = new File(path);
            ImageIO.write(image, "png", outputfile);

            return path;
        } catch (IOException e) {
            e.printStackTrace();
            return null;
        }
    }

    public static void main(String[] args) {

        String path = generateImage("data:image/png;base64,","D:/img/person_img/");

        UplodFtpUtils.uploadFtpFile("192.168.0.116","21","test","test","person_img",path);
    }


}
